import React from 'react';
import { CancelSmallMinor } from '@shopify/polaris-icons';
import { classNames } from '../../utilities/css';
import { useI18n } from '../../utilities/i18n';
import { Icon } from '../Icon';
import { handleMouseUpByBlurring } from '../../utilities/focus';
import { useFeatures } from '../../utilities/features';
import styles from './Tag.scss';
export function Tag({ children, disabled = false, onRemove }) {
    const i18n = useI18n();
    const { unstableGlobalTheming } = useFeatures();
    const className = classNames(disabled && styles.disabled, styles.Tag);
    const ariaLabel = i18n.translate('Polaris.Tag.ariaLabel', {
        children: children || '',
    });
    const buttonClassName = classNames(styles.Button, unstableGlobalTheming && styles.globalTheming);
    return (<span className={className}>
      <span title={children} className={styles.TagText}>
        {children}
      </span>
      <button type="button" aria-label={ariaLabel} className={buttonClassName} onClick={onRemove} onMouseUp={handleMouseUpByBlurring} disabled={disabled}>
        <Icon source={CancelSmallMinor}/>
      </button>
    </span>);
}
